interface SortableColumnProps<TData> {
  column: Column<TData, unknown>
}

function SortableColumn<TData>({ column }: SortableColumnProps<TData>) {
  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: column.id })

  const style = {
    transform: CSS.Transform.toString(transform),
