// multi Edit Form Wrapper
export function ContactMultiEditForm({
  selectedCount,
  onSuccess,
  onCancel,
  updateActionMulti
}: {
  selectedCount: number
  onSuccess?: () => void
  onCancel?: () => void
  updateActionMulti?: (ids: string[], data: Partial<PersonWithRelations>) => Promise<{ success: boolean; error?: string; updatedCount?: number }>
}) {
  const router = useRouter()
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [formData, setFormData] = useState<PersonFormData | null>(null)
  const [companies, setCompanies] = useState<Company[]>([])

  useEffect(() => {
    async function fetchCompanies() {
      const { data, error } = await getCompanies()
      if (error) {
        toast.error("Could not fetch companies.")
        console.error(error)
      } else if (data) {
        setCompanies(data)
      }
    }
    fetchCompanies()
  }, [])

  const handleFormDataChange = useCallback((data: PersonFormData) => {
    setFormData(data)
  }, [])

  const handleSubmit = async () => {
    if (!formData || !updateActionMulti) return

    setIsSubmitting(true)
    try {
      const contactData = transformFormDataToContact(formData)

      // Filter out undefined values for multi edit - only update fields that were actually modified
      const filteredData = Object.fromEntries(
        Object.entries(contactData).filter(([, value]) => {
          if (value === undefined || value === null) return false
          if (typeof value === 'string' && value.trim() === '') return false
          if (Array.isArray(value) && value.length === 0) return false
          return true
        })
      )

      // The updateActionMulti function will be called with the selected contact IDs
      // by the DataTableRowEditMulti component
      const result = await updateActionMulti([], filteredData)

      if (result.success) {
        router.refresh()
        onSuccess?.()
        toast.success("Contacts updated successfully", {
          description: `${result.updatedCount || selectedCount} contact${(result.updatedCount || selectedCount) > 1 ? 's' : ''} updated.`
        })
      } else {
        console.error("Failed to update contacts:", result.error)
        toast.error("Failed to update contacts", { description: result.error })
      }
    } catch (error) {
      console.error("Error updating contacts:", error)
      toast.error("An unexpected error occurred while updating the contacts.")
    } finally {
      setIsSubmitting(false)
    }
  }

  return (
    <div className="h-full flex flex-col">
      <div className="flex-1 overflow-y-auto p-4">

        <PersonForm
          onChange={handleFormDataChange}
          availableCompanies={companies}
          // Start with empty values for multi edit
          initialFirstName=""
          initialLastName=""
          initialEmails={[]}
          initialPhones={[]}
          initialCity=""
          initialState=""
          initialCompany=""
          initialDescription=""
          initialLinkedin=""
          initialJobTitle=""
        />
      </div>

      <div className="flex justify-between gap-2 p-4 border-t bg-background">
        <Button
          type="button"
          variant="outline"
          onClick={onCancel}
          className="w-1/2"
        >
          <X className="size-4 shrink-0" /> Cancel
        </Button>
        <Button
          onClick={handleSubmit}
          disabled={isSubmitting || !formData}
          className="w-1/2"
        >
          <Save className="size-4 shrink-0" />
          {isSubmitting ? "Updating..." : `Update ${selectedCount} Contact${selectedCount > 1 ? 's' : ''}`}
        </Button>
      </div>
    </div>
  )
}

"use server"

import { createClient } from "@/lib/supabase/server"
import { revalidatePath } from "next/cache"
import { Person } from "./validations"
import { getCompanies as dbGetCompanies } from "./queries"
import type { SupabaseClient } from '@supabase/supabase-js'

interface PersonWithExtras extends Omit<Person, "id" | "created_at" | "updated_at"> {
  _emails?: string[]
  _phones?: string[]
  company_name?: string
}

async function findOrCreateCompany(supabase: SupabaseClient, companyName: string | undefined): Promise<string | null> {
    if (!companyName) return null

    // Check if company exists
    const { data: existingCompany, error: findError } = await supabase
        .schema("registry")
        .from("companies")
        .select("id")
        .eq("name", companyName)
        .single()

    if (findError && findError.code !== 'PGRST116') { // PGRST116: no rows found
        console.error("Error finding company:", findError)
        throw new Error(findError.message)
    }

    if (existingCompany) {
        return existingCompany.id
    }

    // Create company if it doesn't exist
    const { data: newCompany, error: createError } = await supabase
        .schema("registry")
        .from("companies")
        .insert({ name: companyName })
        .select("id")
        .single()

    if (createError) {
        console.error("Error creating company:", createError)
        throw new Error(createError.message)
    }

    return newCompany.id
}

export async function createPerson(data: Record<string, unknown>) {
  const supabase = await createClient()

  try {
    // Extract emails, phones and company from the data
    const { _emails, _phones, company_name, ...contactData } = data as PersonWithExtras

    if (company_name) {
      const companyId = await findOrCreateCompany(supabase, company_name)
      contactData.company_id = companyId
    }

    // Start a transaction by creating the contact first
    const { data: newContact, error: contactError } = await supabase
      .schema("registry")
      .from("contacts")
      .insert([contactData])
      .select()
      .single()

    if (contactError) {
      console.error("Error creating contact:", contactError)
      return { success: false, error: contactError.message }
    }

    // Create emails if provided
    if (_emails && _emails.length > 0) {
      const emailsToInsert = _emails.map((email, index) => ({
        contact_id: newContact.id,
        email: email,
        display_order: index
      }))

      const { error: emailError } = await supabase
        .schema("registry")
        .from("contact_emails")
        .insert(emailsToInsert)

      if (emailError) {
        console.error("Error creating emails:", emailError)
        // Optionally rollback by deleting the contact
        await supabase.schema("registry").from("contacts").delete().eq("id", newContact.id)
        return { success: false, error: emailError.message }
      }
    }

    // Create phones if provided
    if (_phones && _phones.length > 0) {
      const phonesToInsert = _phones.map((phone, index) => ({
        contact_id: newContact.id,
        phone: phone,
        display_order: index
      }))

      const { error: phoneError } = await supabase
        .schema("registry")
        .from("contact_phones")
        .insert(phonesToInsert)

      if (phoneError) {
        console.error("Error creating phones:", phoneError)
        // Optionally rollback
        await supabase.schema("registry").from("contacts").delete().eq("id", newContact.id)
        return { success: false, error: phoneError.message }
      }
    }

    revalidatePath("/contacts")
    return { success: true, data: newContact }
  } catch (error) {
    console.error("Unexpected error creating contact:", error)
    return { success: false, error: "An unexpected error occurred" }
  }
}

export async function updatePerson(id: string, data: Record<string, unknown>) {
  const supabase = await createClient()

  try {
    // Extract emails and phones from the data
    const { _emails, _phones, company_name, ...contactData } = data as PersonWithExtras

    if (company_name !== undefined) {
      const companyId = await findOrCreateCompany(supabase, company_name)
      contactData.company_id = companyId
    }

    // Update the contact
    const { data: updatedContact, error: contactError } = await supabase
      .schema("registry")
      .from("contacts")
      .update(contactData)
