"use client"

import * as React from "react"
import { useRouter, useSearchParams, usePathname } from "next/navigation"
import {
  ColumnDef,
  ColumnFiltersState,
  SortingState,
  VisibilityState,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  PaginationState,
} from "@tanstack/react-table"

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table"

import { DataTablePagination } from "./data-table-pagination"
import DataTableToolbar from "./data-table-toolbar"
import { 
  DataTableState, 
  serializeTableState, 
  updateSearchParams 
} from "@/lib/data-table"

/**
 * Props for the DataTable component
 */
export interface DataTableProps {
  /** Array of column definitions that define the table structure */
  columns: ColumnDef<Record<string, unknown>, unknown>[]
  /** Array of data objects to display in the table */
  data: Record<string, unknown>[]
  /** Initial state for the table including pagination, sorting, filters, etc. */
  initialState?: Partial<DataTableState>
  /** Total number of pages for server-side pagination */
  pageCount?: number
  /** Function to handle bulk deletion of rows */
  deleteAction?: (ids: string[]) => Promise<{ success: boolean; error?: string; deletedCount?: number }>
  /** Function to handle creation of new rows */
  createAction?: (data: Record<string, unknown>) => Promise<{ success: boolean; error?: string }>
  /** Function to handle updating existing rows */
  updateAction?: (id: string, data: Record<string, unknown>) => Promise<{ success: boolean; error?: string }>
}

interface DataTableInternalProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[]
  data: TData[]
  initialState?: Partial<DataTableState>
  pageCount?: number
  deleteAction?: (ids: string[]) => Promise<{ success: boolean; error?: string; deletedCount?: number }>
  createAction?: (data: Partial<TData>) => Promise<{ success: boolean; error?: string }>
  updateAction?: (id: string, data: Partial<TData>) => Promise<{ success: boolean; error?: string }>
}

export function DataTable<TData, TValue>({
  columns,
  data,
  initialState,
  pageCount,
  deleteAction,
  createAction,
  updateAction,
}: DataTableInternalProps<TData, TValue>) {
  const router = useRouter()
  const pathname = usePathname()
  const searchParams = useSearchParams()

  const [sorting, setSorting] = React.useState<SortingState>(
    initialState?.sorting ?? []
  )
  const [columnFilters, setColumnFilters] = React.useState<ColumnFiltersState>(
    initialState?.columnFilters ?? []
  )
  const [columnVisibility, setColumnVisibility] = React.useState<VisibilityState>(
    initialState?.columnVisibility ?? {}
  )
  const [rowSelection, setRowSelection] = React.useState({})
  const [pagination, setPagination] = React.useState<PaginationState>(
    initialState?.pagination ?? { pageIndex: 0, pageSize: 10 }
  )
  const [columnOrder, setColumnOrder] = React.useState<string[]>(
    initialState?.columnOrder ?? []
  )

  // Sync state changes to URL
  React.useEffect(() => {
    const currentState: DataTableState = {
      pagination,
      sorting,
      columnFilters,
      columnVisibility,
      columnOrder,
    }

    const newParams = serializeTableState(currentState)
    const updatedSearchParams = updateSearchParams(searchParams, newParams)

    // Only update URL if parameters actually changed
    const currentUrl = `${pathname}?${searchParams.toString()}`
    const newUrl = `${pathname}?${updatedSearchParams.toString()}`

    if (currentUrl !== newUrl) {
      router.replace(newUrl, { scroll: false })
    }
  }, [pagination, sorting, columnFilters, columnVisibility, columnOrder, router, pathname, searchParams])

  const table = useReactTable({
    data,
    columns,
    pageCount: pageCount ?? -1,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    onSortingChange: setSorting,
    getSortedRowModel: getSortedRowModel(),
    onColumnFiltersChange: setColumnFilters,
    getFilteredRowModel: getFilteredRowModel(),
    onColumnVisibilityChange: setColumnVisibility,
    onRowSelectionChange: setRowSelection,
    onPaginationChange: setPagination,
    onColumnOrderChange: setColumnOrder,
    enableMultiSort: true,
    manualPagination: true,
    manualFiltering: true,
    manualSorting: true,
    state: {
      sorting,
      columnFilters,
      columnVisibility,
      rowSelection,
      pagination,
      columnOrder,
    },
  })

  return (
    <div>
        <div className="pb-2">
            <DataTableToolbar 
              table={table} 
              deleteAction={deleteAction} 
              createAction={createAction}
              updateAction={updateAction}
            />
        </div>

        <div className="rounded-md border">
            <Table>
                <TableHeader>
                {table.getHeaderGroups().map((headerGroup) => (
                    <TableRow key={headerGroup.id}>
                    {headerGroup.headers.map((header) => {
                        return (
                        <TableHead key={header.id}>
                            {header.isPlaceholder
                            ? null
                            : flexRender(
                                header.column.columnDef.header,
                                header.getContext()
                                )}
                        </TableHead>
                        )
                    })}
                    </TableRow>
                ))}
                </TableHeader>
                <TableBody>
                {table.getRowModel().rows?.length ? (
                    table.getRowModel().rows.map((row) => (
                    <TableRow
                        key={row.id}
                        data-state={row.getIsSelected() && "selected"}
                    >
                        {row.getVisibleCells().map((cell) => (
                        <TableCell key={cell.id}>
                            {flexRender(cell.column.columnDef.cell, cell.getContext())}
                        </TableCell>
                        ))}
                    </TableRow>
                    ))
                ) : (
                    <TableRow>
                    <TableCell colSpan={columns.length} className="h-24 text-center">
                        No results.
                    </TableCell>
                    </TableRow>
                )}
                </TableBody>
            </Table>
        </div>

        <div className="pt-2">
            <DataTablePagination table={table} />
        </div>
    </div>
  )
}

"use client"

import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Separator } from "@/components/ui/separator";
import { Badge } from "@/components/ui/badge";
import { ListFilter, Plus, ChevronRight } from "lucide-react";
import DataTableFilterItem from "./data-table-filter-item";
import { useState, useEffect } from "react";
import {
  DndContext,
  closestCenter,
  KeyboardSensor,
  PointerSensor,
  useSensor,
  useSensors,
  DragEndEvent,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  sortableKeyboardCoordinates,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { Table } from "@tanstack/react-table";
import type { ExtendedColumnFilter, FilterVariant, FilterOperator } from "@/lib/data-table";

// Generate a random ID
function generateId(length: number = 8): string {
  return Math.random().toString(36).substring(2, 2 + length);
}

export default function DataTableFilter<TData>({ table }: { table: Table<TData> }) {
  const [filters, setFilters] = useState<ExtendedColumnFilter<TData>[]>([]);
  const [logicalOperator, setLogicalOperator] = useState<"and" | "or">("and");
  const [open, setOpen] = useState(false);

  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Sync filters with table's column filters state
  useEffect(() => {
    const currentFilters = table.getState().columnFilters;
    if (currentFilters.length > 0) {
      const newFilters: ExtendedColumnFilter<TData>[] = currentFilters.map((filter, index) => {
        // Get column metadata to determine variant
        const column = table.getColumn(filter.id);
        const columnMeta = column?.columnDef.meta;
        const variant: FilterVariant = columnMeta?.variant ?? "text";

        // Extract filter details from the value object
        const filterValue = filter.value as { operator?: string; value?: unknown; variant?: string } | undefined;
        const operator = filterValue?.operator ?? "iLike";
        const value = filterValue?.value ?? "";

        return {
          filterId: `${filter.id}-${index}`, // Create unique ID
          id: filter.id as Extract<keyof TData, string>,
          value: value as string | number | boolean | string[] | Date,
          operator: operator as FilterOperator,
          variant: variant,
        };
      });
      setFilters(newFilters);
    } else {
      // If no filters are applied, show one empty filter item
      setFilters([
        {
          filterId: generateId(),
          id: "" as Extract<keyof TData, string>,
          value: "",
          operator: "iLike",
          variant: "text",
        },
      ]);
    }
  }, [table.getState().columnFilters]);

  const addFilter = () => {
    const newFilter: ExtendedColumnFilter<TData> = {
      filterId: generateId(),
      id: "" as Extract<keyof TData, string>,
      value: "",
      operator: "iLike",
      variant: "text",
    };
    setFilters([...filters, newFilter]);
  };

  const removeFilter = (filterId: string) => {
    setFilters(filters.filter((f) => f.filterId !== filterId));
  };

  const handleDragEnd = (event: DragEndEvent) => {
    const { active, over } = event;
    if (over && active.id !== over.id) {
      setFilters((items) => {
        const oldIndex = items.findIndex((item) => item.filterId === active.id);
        const newIndex = items.findIndex((item) => item.filterId === over.id);
        return arrayMove(items, oldIndex, newIndex);
      });
    }
  };

  const updateFilter = (filterId: string, newFilter: ExtendedColumnFilter<TData>) => {
    setFilters(
      filters.map((f) => (f.filterId === filterId ? newFilter : f))
    );
  };

  const applyFilters = () => {
    // Get valid filters (those with column selected and either have value or are empty/not empty operators)
    const validFilters = filters.filter((f) => 
      f.id && f.operator && (
        (f.value !== "" && f.value !== null && f.value !== undefined) || 
        ["isEmpty", "isNotEmpty"].includes(f.operator)
      )
    );

    // Convert to TanStack table format with custom filter functions
    const columnFilters = validFilters.map((filter) => {
      return {
        id: filter.id as string,
        value: {
          operator: filter.operator,
          value: filter.value,
          variant: filter.variant
        }
      };
    });

    table.setColumnFilters(columnFilters);
    setOpen(false); // Close the popover after applying filters
  };

  // Get the actual number of applied filters from the table state
  const appliedFilterCount = table.getState().columnFilters.length;

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button variant="outline">
          <ListFilter className="h-4 w-4" />
          <div>Filter</div>
          <Badge variant="secondary">
            {appliedFilterCount}
          </Badge>
        </Button>
      </PopoverTrigger>
      <PopoverContent align="start" className="w-fit p-3">
        <div className="flex w-full flex-col gap-3">
          <p className="text-sm font-medium text-muted-foreground">
            Filter by:
          </p>
          <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleDragEnd}
          >
            <SortableContext
              items={filters.map((item) => item.filterId)}
              strategy={verticalListSortingStrategy}
            >
              <div className="flex flex-col gap-3">
                {filters.map((filter, index) => (
                  <DataTableFilterItem
                    key={filter.filterId}
                    table={table}
                    filter={filter}
                    onFilterChange={(newFilter) => updateFilter(filter.filterId, newFilter)}
                    onRemove={() => removeFilter(filter.filterId)}
                    index={index}
                    logicalOperator={logicalOperator}
                    onLogicalOperatorChange={setLogicalOperator}
                  />
                ))}
              </div>
            </SortableContext>
          </DndContext>

          <Separator />

          <div className="flex flex-row items-center justify-between gap-3">
            <Button
              variant="secondary"
              className="flex flex-row items-center h-8"
              onClick={addFilter}
            >
              <Plus className="h-4 w-4" />
              Add
            </Button>
            <Button
              variant="default"
              className="flex flex-row items-center h-8"
              onClick={applyFilters}
            >
              Apply
              <ChevronRight className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
}

"use client"

import { Button } from "@/components/ui/button"
import {
  Command,
  CommandEmpty,
  CommandGroup,
  CommandInput,
  CommandItem,
  CommandList,
} from "@/components/ui/command"
import { Input } from "@/components/ui/input"
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover"
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select"
import { Badge } from "@/components/ui/badge"
import { Checkbox } from "@/components/ui/checkbox"
import { Calendar } from "@/components/ui/calendar"
import { CalendarDays, ChevronsUpDown, GripVertical, X } from "lucide-react"
import { useState } from "react"
import { useSortable } from "@dnd-kit/sortable"
import { CSS } from "@dnd-kit/utilities"
import { Table } from "@tanstack/react-table"
import { cn } from "@/lib/utils"
import { dataTableConfig } from "@/lib/data-table"
import type { ExtendedColumnFilter, FilterVariant, FilterOperator } from "@/lib/data-table"
import { InputNumber } from "@/components/ui/input-number"

// Format date utility
function formatDate(
  date: Date | string | number | undefined,
  opts: Intl.DateTimeFormatOptions = {},
) {
  if (!date) return "";

  try {
    return new Intl.DateTimeFormat("en-US", {
      month: opts.month ?? "long",
      day: opts.day ?? "numeric", 
      year: opts.year ?? "numeric",
      ...opts,
    }).format(new Date(date));
  } catch {
    return "";
  }
}

// Get filter operators for a specific variant
function getFilterOperators(filterVariant: FilterVariant) {
  const operatorMap: Record<FilterVariant, { label: string; value: FilterOperator }[]> = {
    text: dataTableConfig.textOperators,
    number: dataTableConfig.numericOperators,
    range: dataTableConfig.numericOperators,
    date: dataTableConfig.dateOperators,
    dateRange: dataTableConfig.dateOperators,
    boolean: dataTableConfig.booleanOperators,
    select: dataTableConfig.selectOperators,
    multiSelect: dataTableConfig.multiSelectOperators,
  };

  return operatorMap[filterVariant] ?? dataTableConfig.textOperators;
}

// Get default filter operator for a variant
function getDefaultFilterOperator(filterVariant: FilterVariant): FilterOperator {
  const operators = getFilterOperators(filterVariant);
  return operators[0]?.value ?? "iLike";
}

interface DataTableFilterItemProps<TData> {
  table: Table<TData>
  filter: ExtendedColumnFilter<TData>
  onFilterChange: (filter: ExtendedColumnFilter<TData>) => void
  onRemove: () => void
  index: number
  logicalOperator: "and" | "or"
  onLogicalOperatorChange: (value: "and" | "or") => void
}

export default function DataTableFilterItem<TData>({
  table,
  filter,
  onFilterChange,
  onRemove,
  index,
  logicalOperator,
  onLogicalOperatorChange,
}: DataTableFilterItemProps<TData>) {
  const [columnOpen, setColumnOpen] = useState(false)
  const [operatorOpen, setOperatorOpen] = useState(false)
  const [valueOpen, setValueOpen] = useState(false)
  const [calendarOpen, setCalendarOpen] = useState(false)

  const {
    attributes,
    listeners,
    setNodeRef,
    transform,
    transition,
    isDragging,
  } = useSortable({ id: filter.filterId })

  const style = {
    transform: CSS.Transform.toString(transform),
    transition,
    opacity: isDragging ? 0.5 : 1,
  }

  const columns = table
    .getAllColumns()
    .filter(
      (column) =>
        column.getCanFilter() &&
        column.id !== "select" &&
        column.id !== "actions"
    )

  const selectedColumn = columns.find((col) => col.id === filter.id)
  const columnMeta = selectedColumn?.columnDef.meta
  const filterVariant: FilterVariant = columnMeta?.variant ?? "text"
  const operators = getFilterOperators(filterVariant)

  const renderLogicalOperator = () => {
    if (index === 0) {
      return <div className="w-16 text-center text-sm font-medium text-muted-foreground">Where</div>
    }
    if (index === 1) {
      return (
        <Button
          variant="secondary"
          size="sm"
          className="w-16 capitalize"
          onClick={() =>
            onLogicalOperatorChange(logicalOperator === "and" ? "or" : "and")
          }
        >
          {logicalOperator}
        </Button>
      )
    }
    return <div className="w-16 text-center text-sm font-medium text-muted-foreground capitalize">{logicalOperator}</div>
  }

  const renderValueInput = () => {
    const placeholder = columnMeta?.placeholder ?? "Enter value..."

    // Disable input for isEmpty and isNotEmpty operators
    const isEmptyOperator = ["isEmpty", "isNotEmpty"].includes(filter.operator)
    if (isEmptyOperator) {
      return (
        <div className="w-40 flex items-center justify-center text-sm text-muted-foreground border rounded px-3 py-2">
          No value needed
        </div>
      )
    }

    switch (filterVariant) {
      case "select": {
        const options = columnMeta?.options ?? []
        return (
          <Popover open={valueOpen} onOpenChange={setValueOpen}>
            <PopoverTrigger asChild>
              <Button variant="outline" className="w-40 justify-start">
                {filter.value ? 
                  (options.find(option => option.value === filter.value)?.label ?? String(filter.value))
                  : "Select value..."
                }
                <ChevronsUpDown className="ml-auto h-4 w-4 shrink-0 opacity-50" />
              </Button>
            </PopoverTrigger>
            <PopoverContent className="w-40 p-0" align="start">
              <Command>
                <CommandInput placeholder="Search..." />
                <CommandList>
                  <CommandEmpty>No options found.</CommandEmpty>
                  <CommandGroup>
                    {options.map((option) => (
                      <CommandItem
                        key={option.value}
                        onSelect={() => {
                          onFilterChange({ ...filter, value: option.value })
                          setValueOpen(false)
                        }}
                      >
                        {option.icon && <option.icon className="mr-2 h-4 w-4" />}
                        {option.label}
                      </CommandItem>
                    ))}
                  </CommandGroup>
                </CommandList>
              </Command>
            </PopoverContent>
          </Popover>
        )
      }

      case "multiSelect": {
        const options = columnMeta?.options ?? []
        const selectedValues = Array.isArray(filter.value) ? filter.value : []

        return (
          <Popover open={valueOpen} onOpenChange={setValueOpen}>
            <PopoverTrigger asChild>
              <Button variant="outline" className="w-40 justify-start">
                {selectedValues.length > 0 ? (
                  <div className="flex flex-wrap gap-1">
                    {selectedValues.slice(0, 2).map((value) => (
                      <Badge key={String(value)} variant="secondary" className="text-xs">
                        {(options.find(option => option.value === value)?.label ?? String(value))}
                      </Badge>
                    ))}
                    {selectedValues.length > 2 && (
                      <Badge variant="secondary" className="text-xs">
                        +{selectedValues.length - 2}
                      </Badge>
                    )}
                  </div>
                ) : (
                  "Select values..."
                )}
                <ChevronsUpDown className="ml-auto h-4 w-4 shrink-0 opacity-50" />
              </Button>
            </PopoverTrigger>
            <PopoverContent className="w-40 p-0" align="start">
              <Command>
                <CommandInput placeholder="Search..." />
                <CommandList>
                  <CommandEmpty>No options found.</CommandEmpty>
                  <CommandGroup>
                    {options.map((option) => (
                      <CommandItem
                        key={option.value}
                        onSelect={() => {
                          const newValues = selectedValues.includes(option.value)
                            ? selectedValues.filter(v => v !== option.value)
                            : [...selectedValues, option.value]
                          onFilterChange({ ...filter, value: newValues })
                        }}
                      >
                        <Checkbox
                          checked={selectedValues.includes(option.value)}
                          className="mr-2"
                        />
                        {option.icon && <option.icon className="mr-2 h-4 w-4" />}
                        {option.label}
                      </CommandItem>
                    ))}
                  </CommandGroup>
                </CommandList>
              </Command>
            </PopoverContent>
          </Popover>
        )
      }

      case "boolean": {
        return (
          <Select
            value={filter.value as string}
            onValueChange={(value) => onFilterChange({ ...filter, value })}
          >
            <SelectTrigger className="w-40">
              <SelectValue placeholder="Select..." />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="true">True</SelectItem>
              <SelectItem value="false">False</SelectItem>
            </SelectContent>
          </Select>
        )
      }

      case "date": {
        const date = filter.value ? new Date(filter.value as string) : undefined
        return (
          <Popover open={calendarOpen} onOpenChange={setCalendarOpen}>
            <PopoverTrigger asChild>
              <Button
                variant="outline"
                className={cn(
                  "w-40 justify-start text-left font-normal",
                  !date && "text-muted-foreground"
                )}
              >
                <CalendarDays className="mr-2 h-4 w-4" />
                {date ? formatDate(date) : "Pick a date"}
              </Button>
            </PopoverTrigger>
            <PopoverContent className="w-auto p-0" align="start">
              <Calendar
                mode="single"
                selected={date}
                onSelect={(selectedDate) => {
                  onFilterChange({ 
                    ...filter, 
                    value: selectedDate ? selectedDate.toISOString() : "" 
                  })
                  setCalendarOpen(false)
                }}
                initialFocus
              />
            </PopoverContent>
          </Popover>
        )
      }

      case "number": {
        const unit = columnMeta?.unit

        // Handle "is between" operator with two inputs
        if (filter.operator === "isBetween") {
          const values = Array.isArray(filter.value) ? filter.value : ["", ""]
          return (
            <div className="flex gap-2 w-80">
              <InputNumber
                value={values[0] || ""}
                onChange={(value) => {
                  const newValues = [String(value || ""), values[1] || ""]
                  onFilterChange({ ...filter, value: newValues })
                }}
                placeholder="Min"
                unit={unit}
                className="flex-1"
              />
              <div className="flex items-center text-sm text-muted-foreground">and</div>
              <InputNumber
                value={values[1] || ""}
                onChange={(value) => {
                  const newValues = [values[0] || "", String(value || "")]
                  onFilterChange({ ...filter, value: newValues })
                }}
                placeholder="Max"
                unit={unit}
                className="flex-1"
              />
            </div>
          )
        }

        return (
          <InputNumber
            value={filter.value as string}
            onChange={(value) => onFilterChange({ ...filter, value: String(value || "") })}
            placeholder={placeholder}
            unit={unit}
            className="w-40"
          />
        )
      }

      default: {
        return (
          <Input
            placeholder={placeholder}
            value={filter.value as string}
            onChange={(e) => onFilterChange({ ...filter, value: e.target.value })}
            className="w-40"
          />
        )
      }
    }
  }

  return (
    <div
      ref={setNodeRef}
      style={style}
      className="flex flex-row items-center gap-2"
    >
      <Button
        variant="ghost"
        size="icon"
        {...attributes}
        {...listeners}
        className="cursor-grab active:cursor-grabbing"
      >
        <GripVertical className="h-4 w-4" />
      </Button>

      {renderLogicalOperator()}

      <Popover open={columnOpen} onOpenChange={setColumnOpen}>
        <PopoverTrigger asChild>
          <Button variant="outline" className="w-32 justify-start">
            <span className="truncate">
              {(selectedColumn?.columnDef.meta?.label ?? filter.id) || "Column"}
            </span>
            <ChevronsUpDown className="ml-auto h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-32 p-0" align="start">
          <Command>
            <CommandInput placeholder="Search..." />
            <CommandList>
              <CommandEmpty>No columns found.</CommandEmpty>
              <CommandGroup>
                {columns.map((column) => (
                  <CommandItem
                    key={column.id}
                    onSelect={() => {
                      const newVariant = column.columnDef.meta?.variant ?? "text"
                      const newOperator = getDefaultFilterOperator(newVariant)
                      onFilterChange({
                        ...filter,
                        id: column.id as Extract<keyof TData, string>,
                        variant: newVariant,
                        operator: newOperator,
                        value: "",
                      })
                      setColumnOpen(false)
                    }}
                  >
                    {(column.columnDef.meta?.label ?? column.id)}
                  </CommandItem>
                ))}
              </CommandGroup>
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>

      <Popover open={operatorOpen} onOpenChange={setOperatorOpen}>
        <PopoverTrigger asChild>
          <Button variant="outline" className="w-32 justify-start">
            <span className="truncate">
              {(operators.find(op => op.value === filter.operator)?.label ?? "Operator")}
            </span>
            <ChevronsUpDown className="ml-auto h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-[9rem] p-0" align="start">
          <Command>
            <CommandList>
              <CommandGroup>
                {operators.map((operator) => (
                  <CommandItem
                    key={operator.value}
                    onSelect={() => {
                      onFilterChange({ ...filter, operator: operator.value })
                      setOperatorOpen(false)
                    }}
                  >
                    {operator.label}
                  </CommandItem>
                ))}
              </CommandGroup>
            </CommandList>
          </Command>
        </PopoverContent>
      </Popover>

      {renderValueInput()}

      <Button variant="ghost" size="icon" onClick={onRemove}>
        <X className="h-4 w-4" />
      </Button>
    </div>
  )
}

import { Table } from "@tanstack/react-table"
import {
  ChevronLeft,
  ChevronRight,
  ChevronsLeft,
  ChevronsRight,
} from "lucide-react"

import { Button } from "@/components/ui/button"
import {
