"use server"

import { createClient } from "@/lib/supabase/server"
import { revalidatePath } from "next/cache"
import { Contact } from "./validations"

export async function createContact(data: Omit<Contact, "id" | "created_at" | "updated_at">) {
  const supabase = await createClient()

  try {
    const { data: newContact, error } = await supabase
      .schema("registry")
      .from("contacts")
      .insert([data])
      .select()
      .single()

    if (error) {
      console.error("Error creating contact:", error)
      return { success: false, error: error.message }
    }

    revalidatePath("/new-contacts")
    return { success: true, data: newContact }
  } catch (error) {
    console.error("Unexpected error creating contact:", error)
    return { success: false, error: "An unexpected error occurred" }
  }
}

export async function updateContact(id: string, data: Partial<Omit<Contact, "id" | "created_at" | "updated_at">>) {
  const supabase = await createClient()

  try {
    const { data: updatedContact, error } = await supabase
      .schema("registry")
      .from("contacts")
      .update(data)
      .eq("id", id)
      .select()
      .single()

    if (error) {
      console.error("Error updating contact:", error)
      return { success: false, error: error.message }
    }

    revalidatePath("/new-contacts")
    return { success: true, data: updatedContact }
  } catch (error) {
    console.error("Unexpected error updating contact:", error)
    return { success: false, error: "An unexpected error occurred" }
  }
}

export async function deleteContacts(contactIds: string[]) {
  const supabase = await createClient()

  try {
    const { error } = await supabase
      .schema("registry")
      .from("contacts")
      .delete()
      .in("id", contactIds)

    if (error) {
      console.error("Error deleting contacts:", error)
      return { success: false, error: error.message }
    }

    revalidatePath("/new-contacts")
    return { success: true, deletedCount: contactIds.length }
  } catch (error) {
    console.error("Unexpected error deleting contacts:", error)
    return { success: false, error: "An unexpected error occurred" }
  }
}

import { createClient } from "@/lib/supabase/server"
import { parseSearchParams, SearchParams } from "@/lib/data-table"
import { ContactWithRelations } from "./validations"
import { PostgrestError } from "@supabase/supabase-js"

export async function getContacts(searchParams: SearchParams): Promise<{
  data: ContactWithRelations[],
  count: number,
  error: PostgrestError | null
}> {
  const supabase = await createClient()
  const {
    pagination,
    sorting,
    columnFilters
  } = parseSearchParams(searchParams)

  const { pageIndex, pageSize } = pagination ?? { pageIndex: 0, pageSize: 10 }
  const sort = sorting ?? []
  const filters = columnFilters ?? []

  let query = supabase
    .schema("registry")
    .from("contacts")
    .select(`
      *,
      company:companies(*),
      emails:contact_emails(*),
      phones:contact_phones(*)
    `, { count: "exact" })

  // Sorting
  if (sort.length > 0) {
    sort.forEach(s => {
      // Handle computed columns and related data sorting
      switch (s.id) {
        case "display_name":
          query = query.order("first_name", { ascending: !s.desc })
          break
        case "company_name":
          // Note: Sorting by related table requires a different approach
          // For now, we'll sort by company_id and handle display sorting client-side
          query = query.order("company_id", { ascending: !s.desc })
          break
        case "primary_email":
        case "primary_phone":
          // These are computed from related tables, sort by contact name instead
          query = query.order("first_name", { ascending: !s.desc })
          break
        case "location":
          query = query.order("city", { ascending: !s.desc })
          break
        default:
          query = query.order(s.id, { ascending: !s.desc })
      }
    })
  } else {
    query = query.order("first_name", { ascending: true })
  }

  // Filtering
  filters.forEach(filter => {
    const { id: columnId, value: filterValue } = filter
    if (typeof filterValue === 'object' && filterValue !== null && 'operator' in filterValue) {
      const { operator, value } = filterValue as { operator: string, value: unknown }

      if (!operator || value === null || value === undefined || (typeof value === 'string' && value === '')) return

      // Handle computed/virtual columns
      switch (columnId) {
        case "display_name":
          // Search in both first_name and last_name
          if (operator === "iLike") {
            query = query.or(`first_name.ilike.%${value}%,last_name.ilike.%${value}%`)
          }
          break
        case "company_name":
          // Filter by company name through the relationship
          // Note: This requires a more complex query structure
          if (operator === "iLike") {
            // We'll need to handle this client-side or use a different approach
            // For now, skip company name filtering in the query
            console.warn("Company name filtering not implemented at query level")
          }
          break
        case "primary_email":
          // Filter contacts that have emails matching the criteria
          // This requires a subquery approach
          if (operator === "iLike") {
            // We'll handle this client-side for now
            console.warn("Email filtering not implemented at query level")
          }
          break
        case "primary_phone":
          // Similar to email filtering
          if (operator === "iLike") {
            console.warn("Phone filtering not implemented at query level")
          }
          break
        case "location":
          // Search in both city and state
          if (operator === "iLike") {
            query = query.or(`city.ilike.%${value}%,state.ilike.%${value}%`)
          }
          break
        default:
          // Handle regular columns
          switch (operator) {
            case "iLike":
              query = query.ilike(columnId, `%${value}%`)
              break
            case "notILike":
              query = query.not(columnId, 'ilike', `%${value}%`)
              break
            case "eq":
              query = query.eq(columnId, value)
              break
            case "ne":
              query = query.neq(columnId, value)
              break
            case "lt":
              query = query.lt(columnId, value)
              break
            case "gt":
              query = query.gt(columnId, value)
              break
            case "inArray":
              query = query.in(columnId, value as (string | number)[])
              break
            case "notInArray":
              query = query.not(columnId, 'in', `(${(value as (string | number)[]).join(',')})`)
              break
            case "isEmpty":
              query = query.or(`${columnId}.is.null,${columnId}.eq.""`)
              break
            case "isNotEmpty":
              query = query.not(columnId, 'is', null).not(columnId, 'eq', '""')
              break
            case "isBetween":
              if (Array.isArray(value) && value.length === 2) {
                query = query.gte(columnId, value[0]).lte(columnId, value[1])
              }
              break
            default:
              break
          }
      }
    }
  })

  // Pagination
  const from = pageIndex * pageSize
  const to = from + pageSize - 1
  query = query.range(from, to)

  const { data, count, error } = await query

  // Sort emails and phones by display_order for consistent primary selection
  const processedData = (data as unknown as ContactWithRelations[])?.map(contact => ({
    ...contact,
    emails: contact.emails?.sort((a, b) => a.display_order - b.display_order) || [],
    phones: contact.phones?.sort((a, b) => a.display_order - b.display_order) || []
  })) || []

  return {
    data: processedData,
    count: count ?? 0,
    error
  }
}

// Base table types (matching your database schema)
export type Contact = {
  id: string;
  created_at?: string;
  updated_at?: string;
  first_name?: string;
  last_name?: string;
  city?: string;
  state?: string;
  company_id?: string | null;
  job_title?: string;
  description?: string;
  linkedin?: string;
}

export type Company = {
  id: string;
  created_at?: string;
  name: string;
  description?: string;
}

export type ContactEmail = {
  id: string;
  contact_id: string;
  email: string;
  display_order: number;
  created_at?: string;
}

export type ContactPhone = {
  id: string;
  contact_id: string;
  phone: string;
  display_order: number;
  created_at?: string;
}

// Enhanced types with relationships
export type ContactWithCompany = Contact & {
  company?: Company;
}

export type ContactWithRelations = Contact & {
  company?: Company;
  emails: ContactEmail[];
  phones: ContactPhone[];
}

// Form-specific types (for your React component)
export type ContactFormData = {
  firstName: string;
  lastName: string;
  emails: string[];
